import 'dart:convert';import 'dart:io';import 'package:admin_app/core/constant/AppLink.dart';import 'package:admin_app/core/functions/printing.dart';import 'package:admin_app/core/functions/status_request.dart';import 'package:admin_app/core/model/Category_model.dart';import 'package:dartz/dartz.dart';import 'package:http/http.dart' as http;import '../../core/class/crud.dart';class CategoriesData {  Crud crud;  CategoriesData(this.crud);  get_categories() async {    try {      return await crud.postData(AppLink.view_cat, {}).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }  add_category(String name, String name_ar, File file) async {    try {      return await crud          .addRequestWithImageOne(        AppLink.add_cat,        {          'name': name,          'name_ar': name_ar,        },        file,      )          .then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      space_print(e);      return Future.error(e);    }  }  delete_category(String id_cat) async {    try {      return await crud.postData(AppLink.delete_cat, {        'cat_id': id_cat,        'image_name': '',      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }  edit_category(String name, String name_ar, File file) async {    try {      return await crud          .addRequestWithImageOne(        AppLink.edit_cat,        {          'name': name,          'name_ar': name_ar,        },        file,      )          .then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      space_print(e);      return Future.error(e);    }  }}