import 'dart:io';import 'package:admin_app/core/class/crud.dart';import 'package:admin_app/core/constant/AppLink.dart';class ItemsData {  Crud crud;  ItemsData(this.crud);  get_items() async {    try {      return await crud.postData(AppLink.view_item, {}).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }  // $item_id = filterRequest("item_id");  // $image_name = filterRequest("image_name");  delete_item(String item_id, String image_name) async {    try {      return await crud.postData(AppLink.delete_item, {        'item_id': item_id,        'image_name': image_name,      }).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }  // $table = "_items";  // $item_name = filterRequest("item_name");  // $item_name_ar = filterRequest("item_name_ar");  // $item_desc = filterRequest("item_desc");  // $item_desc_ar = filterRequest("item_desc_ar");  // $item_discount = filterRequest("item_discount");  // $item_count = filterRequest("item_count");  // $item_active = filterRequest("item_active");  // $item_price = filterRequest("item_price");  // $item_cat = filterRequest("item_cat");  //  // $image_name = imageUpload("../../upload/items","item_image");  add_item(File file, item_name, item_name_ar, item_desc, item_desc_ar,      item_discount, item_count, item_active, item_price, item_cat) async {    try {      return await crud          .addRequestWithImageOne(        AppLink.add_item,        {          'item_name': item_name,          'item_name_ar': item_name_ar,          'item_desc': item_desc,          'item_desc_ar': item_desc_ar,          'item_discount': item_discount,          'item_count': item_count,          'item_active': item_active,          'item_price': item_price,          'item_cat': item_cat,        },        file,      )          .then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }  edit_item(      File file,      item_id,    item_name,    item_name_ar,    item_desc,    item_desc_ar,    item_discount,    item_count,    item_active,    item_price,    item_cat,      image_name_deleted,  ) async {    try {      return await crud          .addRequestWithImageOne(        AppLink.edit_item,        {          'item_id' : item_id,          'item_name': item_name,          'item_name_ar': item_name_ar,          'item_desc': item_desc,          'item_desc_ar': item_desc_ar,          'item_discount': item_discount,          'item_count': item_count,          'item_active': item_active,          'item_price': item_price,          'item_cat': item_cat,          'image_name_deleted': image_name_deleted,        },        file,      )          .then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return Future.error(e);    }  }}