import 'package:admin_app/core/class/crud.dart';import 'package:admin_app/core/constant/AppLink.dart';class OrdersData {  Crud crud;  OrdersData(this.crud);  get_orders() async {    try {      await crud.postData(        AppLink.view_orders,        {},      ).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return {        'status': 'fail',      };    }  }  get_orders_archive() async {    try {      await crud.postData(        AppLink.order_archive,        {},      ).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return {        'status': 'fail',      };    }  }  approve_order(user_id, order_id) async {    try {      await crud.postData(        AppLink.approve,        {          'user_id': user_id,          'order_id': order_id,        },      ).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return {        'status': 'fail',      };    }  }  prepare_order(user_id, order_id) async {    try {      await crud.postData(        AppLink.prepare,        {          'user_id': user_id,          'order_id': order_id,        },      ).then((value) {        return value.fold((l) => l, (r) => r);      });    } catch (e) {      return {        'status': 'fail',      };    }  }}