import 'package:admin_app/controller/categories/add_edit_category_controller.dart';import 'package:admin_app/controller/categories/category_controller.dart';import 'package:admin_app/core/constant/AppLink.dart';import 'package:flutter/material.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:get/get.dart';import 'package:google_fonts/google_fonts.dart';class AddCategoryScreen extends StatelessWidget {  const AddCategoryScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Get.lazyPut(() => AddEditCategoriesController());    return GetBuilder<AddEditCategoriesController>(builder: (logic) {      return Scaffold(          body: Center(        child: Column(          mainAxisSize: MainAxisSize.min,          children: [            GestureDetector(              onTap: () {                logic.is_add == true                    ? logic.add_image(context)                    : logic.edit_category(context);              },              child: Container(                height: 250,                width: 250,                child: logic.get_image(),              ),            ),            text_field(controller: logic.controller_en, is_en: true),            text_field(controller: logic.controller_ar, is_en: false),            ActionChip(              onPressed: () {                logic.add_category(context);              },              backgroundColor: Colors.white60,              label: Padding(                padding: const EdgeInsets.symmetric(                  horizontal: 20,                  vertical: 7,                ),                child: Text(                  logic.is_add == true ? 'Add Category' : 'Edit Category',                  style: GoogleFonts.acme().copyWith(                    fontSize: 18,                  ),                ),              ),            ),          ],        ),      ));    });  }}Widget text_field({  required TextEditingController controller,  required bool is_en,}) {  return Container(    height: 50,    padding: EdgeInsets.symmetric(horizontal: 30),    margin: EdgeInsets.only(      bottom: is_en ? 8 : 20,      top: is_en ? 20 : 8,    ),    child: TextFormField(      controller: controller,      decoration: InputDecoration(        hintText: is_en ? 'English Name Category' : 'Arabic Name Category',        hintStyle: TextStyle(          fontSize: 15,          color: Colors.black,        ),        enabledBorder: OutlineInputBorder(          borderRadius: BorderRadius.circular(15),          borderSide: BorderSide(            color: Colors.black,          ),        ),        focusedBorder: OutlineInputBorder(          borderRadius: BorderRadius.circular(15),          borderSide: BorderSide(            color: Colors.black,          ),        ),      ),    ),  );}