import 'package:admin_app/controller/main_home_controller.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:salomon_bottom_bar/salomon_bottom_bar.dart';class HomeApp extends StatelessWidget {  const HomeApp({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    Get.put(MainHomeController());    return GetBuilder<MainHomeController>(builder: (logic) {      return Scaffold(        appBar: AppBar(          backgroundColor: Colors.black,          elevation: 0,          title: Text(            logic.title_app_bar[logic.current_index],            style: TextStyle(              fontSize: 18,              color: Colors.white,              fontWeight: FontWeight.bold,            ),          ),        ),        body: logic.screens[logic.current_index],        bottomNavigationBar: SalomonBottomBar(          currentIndex: logic.current_index,          onTap: (index) {            logic.change_index(index);          },          items: logic.items,          selectedItemColor: Colors.white,          unselectedItemColor: Colors.grey,          backgroundColor: Colors.black,          duration: Duration(            milliseconds: 500,          ),          curve: Curves.linearToEaseOut,        ),      );    });  }}